  scriptDesc =
  ## (script description) contains a description of the scripts 
  used in a manuscript or other object. 
  [10.7.2.1. Writing]
element scriptDesc {
(model.pLike+ | (summary?, scriptNote*, musicNotation*, crypto*)),
att.typed.attributes,
att.global.attributes,
##
attribute script {
xsd:token { pattern = "[^\p{C}\p{Z}]+" }
}?,
empty
}

